import { Anchor } from 'antd';
import { useState } from 'react';

const { Link } = Anchor;

const _Header = ({ props }) => {
  const [active, setActive] = useState(false);

  const handleClick = () => {
    setActive(!active);
  };

  const scrollToSection = (sectionId) => {
    const section = document.querySelector(sectionId);
    if (section) {
      const offset = section.getBoundingClientRect().top + window.pageYOffset;
      const duration = 500; // ปรับค่าตามความต้องการ
      const startTime = performance.now();

      const easeInOutQuad = (t, b, c, d) => {
        t /= d / 2;
        if (t < 1) return c / 2 * t * t + b;
        t--;
        return -c / 2 * (t * (t - 2) - 1) + b;
      };

      const animateScroll = (currentTime) => {
        const elapsedTime = currentTime - startTime;
        window.scrollTo(0, easeInOutQuad(elapsedTime, window.pageYOffset, offset, duration));
        if (elapsedTime < duration) {
          requestAnimationFrame(animateScroll);
        } else {
          window.scrollTo(0, offset);
        }
      };

      requestAnimationFrame(animateScroll);
    }
  };

  return (
    <>
      <nav className="fixed z-50 w-full flex items-center flex-wrap  h-14  bg-white pt-1 pb-1">
        <div className="lg:w-1/6 xl:w-1/6 2xl:w-1/6 sm:w-3/6 md:w-3/6 pl-3 ">
          <a href="/" className="inline-flex items-center p-2 mr-4 ">
            <img
              className="h-7 w-auto"
              src={
                'assets/images/logo-government.png'
              }
            />
          </a>
        </div>

        <button
          className="pr-3 inline-flex p-3 hover:bg-green-600 rounded lg:hidden text-green-900 ml-auto hover:text-white outline-none mr-1"
          onClick={handleClick}
        >
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        </button>
        <div
          className={`${active ? '' : 'hidden'
            } bg-white w-full lg:inline-flex lg:flex-grow sm:w-6/6 lg:w-4/6 justify-center pl-3 pr-3 content-center`}
        >
          <div className="text-center lg:inline-flex lg:flex-row lg:w-auto w-full lg:items-center justify-center items-start  flex flex-col lg:h-auto">
            <a onClick={() => { handleClick(); scrollToSection('#property') }} className="lg:inline-flex lg:w-auto w-full px-3 py-2 rounded text-green-900 font-bold items-center justify-center   hover:text-green-900">
              คุณสมบัติ
            </a>
            <a href="/#package" className="lg:inline-flex lg:w-auto w-full px-3 py-2 rounded text-green-900 font-bold items-center justify-center  hover:text-green-900">
              แพคเกจ
            </a>
            <a href="/#contact" className="lg:inline-flex lg:w-auto w-full px-3 py-2 rounded text-green-900 font-bold items-center justify-center hover:text-green-900">
              ติดต่อเรา
            </a>
          </div>
          <div className="lg:hidden h-1 border-t-2 border-gray-200"></div>
        </div>
        <div
          className={`${active ? '' : 'hidden'
            }  text-center bg-white w-full lg:inline-flex lg:flex-grow lg:w-1/6 justify-end sm:h-12 md:h-12 px-2 items-center pb-18`}
        >
          <div className="lg:inline-flex lg:flex-row lg:w-auto w-full lg:items-center justify-center items-start  flex flex-col lg:h-auto">
            <a href="/login" className="lg:inline-flex lg:w-auto w-full px-1 py-2 rounded text-green-900 font-bold items-center justify-center  hover:text-green-900">
              เข้าสู่ระบบ
            </a>

            <a href="/register" className="lg:inline-flex lg:w-auto w-full px-2 py-2 rounded h-8 text-white bg-green-600 font-bold items-center justify-center hover:bg-green-600 hover:text-white">
              สมัครใช้งาน
            </a>
            <div className="lg:hidden h-4"></div>
          </div>
        </div>
      </nav>
    </>
  );
};

export default _Header;
