import Link from 'next/link';
import reqwest from 'reqwest';
/* utils */
import { absoluteUrl, checkIsLogin, apiInstance, convertDateDBtoDatePickerNoTime, bytesToSize, convertDatetoThaiDate  } from '../../../middleware/utils';
import Router, { useRouter } from 'next/router';
/* components */
import Layout from '../../../components/layout/LayoutAdmin';
// import UserNav from '../components/navigation/User';
import { useEffect, useState } from 'react'

import {
  Carousel,
  Row,
  Col,
  Typography,
  Image,
  Button,
  Input,
  Card,
  Table,
  Modal,
  notification
} from 'antd';
const { Text, Title } = Typography;

const { TextArea, Search } = Input;


export default function Home(props) {
  const [api, contextHolder] = notification.useNotification();
  const { user, origin } = props;
  const [login, setLogin] = useState(null)
  const [shouldRun, setShouldRun] = useState(true)
  const [data, setData] = useState([])
  const [loading, setLoading] = useState(false)
  const [search, setSearch] = useState("")
  const [visibleModalDelete, setVisibleModalDelete] = useState(false)
  const [dataDelete, setDataDelete] = useState(null)
  const [filters, setFilters] = useState(null)
  const [pagination, setPagination] = useState({
    current: 1,
    pageSize: 10,
  })
  const columns = [
    {
      title: 'วันที่สมัคร',
      dataIndex: '',
      sorter: true,
      key: 'date_created',
      render: (data) => <>
        <span > {convertDatetoThaiDate(convertDateDBtoDatePickerNoTime(data.date_created))} </span>
      </>,
      // render: name => `${name.first} ${name.last}`,
      // width: '20%',
      // defaultSortOrder: 'descend',
    },
    {
      title: 'วันที่หมดอายุ',
      dataIndex: '',
      sorter: true,
      key: 'date_expired',
      render: (data) => <>
        <span > {convertDatetoThaiDate(convertDateDBtoDatePickerNoTime(data.date_expired))} </span>
      </>,
      // render: name => `${name.first} ${name.last}`,
      // width: '20%',
      // defaultSortOrder: 'descend',
    },
    {
      title: 'ชื่อหน่วยงาน',
      dataIndex: 'organization_name',
      sorter: true,
      // render: name => `${name.first} ${name.last}`,
      // width: '20%',
      // defaultSortOrder: 'descend',
    },
    {
      title: 'ใช้พื้นที่',
      dataIndex: '',
      sorter: true,
      key: 'size_used',
      render: (data) => <>
        <span > {bytesToSize(data.size_used)}  </span>
      </>
      // defaultSortOrder: 'descend',
    },
    {
      title: 'package',
      dataIndex: '',
      key: 'package',
      sorter: true,
      render: (data) => <>
        <span > {data.packages.name_package} </span>
      </>,
      filters: [
        {
          text: 'Free Package',
          value: 1,
        },
        {
          text: 'Connect Package',
          value: 2,
        },
        {
          text: 'Standard Package',
          value: 3,
        },
        {
          text: 'Premium Package',
          value: 4,
        },
      ],
      // render: name => `${name.first} ${name.last}`,
      // width: '20%',
      // defaultSortOrder: 'descend',
    },
    {
      title: 'สถานะ',
      dataIndex: '',
      key: 'is_use',
      sorter: true,
      render: (data) => <>
        <span > {data.is_use == 1 ? "ใช้งาน" : "ไม่ใช้งาน"} </span>
      </>,
      // render: name => `${name.first} ${name.last}`,
      // width: '20%',
      // defaultSortOrder: 'descend',
    },
    {
      title: '',
      dataIndex: '',
      key: 'x',
      render: (data) => <>
        <Button type="primary" size={"default"} onClick={() => gotoPage(data.organization_id)} > แก้ไข </Button>
        <Button className="ml-1" type="danger" onClick={() => showModal(data)} size={"default"} > ลบ </Button>
      </>,
    },
    // {
    //   title: 'Gender',
    //   dataIndex: 'gender',
    //   // filters: [
    //   //   { text: 'Male', value: 'male' },
    //   //   { text: 'Female', value: 'female' },
    //   // ],
    //   width: '20%',
    // },
    // {
    //   title: 'Email',
    //   dataIndex: 'email',
    // },
  ];
  const gotoPage = (id) => {
    console.log(id)
    Router.push('/admin/management-organization/organization/' + id);

  };

  useEffect(() => {
    var _filters = []
    pagination.filters = _filters
    fetch({ pagination });
  }, [])

  const handleTableChange = (pagination, filters, sorter) => {
    console.log(sorter)
    setPagination({
      pageSize: pagination.pageSize,
      current: pagination.current
    })
    setFilters(filters.package)
    fetch({
      sortField: sorter.columnKey,
      sortOrder: sorter.order,
      pagination,
      filters: filters.package,
    });
  };
  const onSearch = async (_search) => {
    setLoading(true)
    setSearch(_search)
    setPagination({
